package parser

import (
	"crypto/ecdsa"
	"crypto/elliptic"
	"crypto/rand"
	"encoding/pem"
	"intel/isecl/sqvs/v5/config"
	"intel/isecl/sqvs/v5/resource/domain/mocks"
	"intel/isecl/sqvs/v5/resource/utils"
	testutils "intel/isecl/sqvs/v5/test/utils"
	"io/ioutil"
	"net/http"
	"os"
	"testing"

	"github.com/stretchr/testify/assert"
)

const (
	privatekeyLocation = "../../test/privkey.key"
	testCertPemFile    = "../../test/test-cert.pem"
	pckCertFile        = "../../test/pck-cert.pem"
	testConfigFilePath = "../../test/config.yml"
)

func TestPckCertParser(t *testing.T) {

	QuoteBlob := ""

	skcBlobParsed := ParseQuoteBlob(QuoteBlob)
	skcBlobParsed.GetQuoteBlob()
	quoteObj := NewSGXQuoteParser(skcBlobParsed.GetQuoteBlob())

	pckCertBytes, _ := utils.GetCertPemData(quoteObj.GetQuotePckCertObj())

	scsClient := mocks.NewClientMock(http.StatusOK)
	testConfig := config.Load(testConfigFilePath)

	pckCert := NewPCKCertObj(pckCertBytes, scsClient, testConfig)
	assert.NotNil(t, pckCert)

	caPrivateKey, err := ecdsa.GenerateKey(elliptic.P256(), rand.Reader)
	if err != nil {
		log.Fatalf("Failed to generate KeyPair %v", err)
	}

	pckCert.GenPckCertRequiredExtMap()

	pckCert.GenPckCertRequiredSgxExtMap()

	extMap := pckCert.GetPckCertRequiredExtMap()
	assert.NotNil(t, extMap)

	pckCertRequiredSgxExtMap := pckCert.GetPckCertRequiredSgxExtMap()
	assert.NotNil(t, pckCertRequiredSgxExtMap)

	// create CertBlob
	testutils.CreateTestCertificate(testCertPemFile, "TEST, INC.", caPrivateKey, true, nil)
	trustedSGXRootCABytes, err := ioutil.ReadFile(testCertPemFile)
	assert.Nil(t, err)

	testCertPem, _ := pem.Decode(trustedSGXRootCABytes)
	assert.NotNil(t, testCertPem)

	err = pckCert.GenCertObj(pem.EncodeToMemory(testCertPem))
	assert.Nil(t, err)

	fmspc := pckCert.GetFmspcValue()
	assert.NotNil(t, fmspc)

	tcbLevels := pckCert.GetPckCertTcbLevels()
	assert.NotNil(t, tcbLevels)

	err = pckCert.ParseFMSPCValue()
	assert.Nil(t, err)

	err = pckCert.ParseTcbExtensions()
	assert.Nil(t, err)

	pubKey := pckCert.GetPCKPublicKey()
	assert.NotNil(t, pubKey)

	pckCrlURLs := pckCert.GetPckCrlURL()
	assert.NotNil(t, pckCrlURLs)

	pckCrlObj := pckCert.GetPckCrlObj()
	assert.NotNil(t, pckCrlObj)

	pckCrlInterCaList := pckCert.GetPckCrlInterCaList()
	assert.NotNil(t, pckCrlInterCaList)

	pckCrlRootCaList := pckCert.GetPckCrlRootCaList()
	assert.NotNil(t, pckCrlRootCaList)

	err = pckCert.ParsePckCrl()
	assert.Nil(t, err)

	// Remove test files and the end.
	os.Remove(testCertPemFile)
	os.Remove(privatekeyLocation)
}

func TestNewPCKCertObj(t *testing.T) {
	caPrivateKey, err := ecdsa.GenerateKey(elliptic.P256(), rand.Reader)
	if err != nil {
		log.Fatalf("Failed to generate KeyPair %v", err)
	}

	// create CertBlob
	testutils.CreateTestCertificate(testCertPemFile, "TEST, INC.", caPrivateKey, true, nil)

	trustedSGXRootCABytes, err := ioutil.ReadFile(testCertPemFile)
	assert.Nil(t, err)

	testCertPem, _ := pem.Decode(trustedSGXRootCABytes)
	assert.NotNil(t, testCertPem)
	got := NewPCKCertObj(pem.EncodeToMemory(testCertPem), nil, nil)
	assert.Nil(t, got)
	QuoteBlob := ""

	skcBlobParsed := ParseQuoteBlob(QuoteBlob)
	skcBlobParsed.GetQuoteBlob()
	quoteObj := NewSGXQuoteParser(skcBlobParsed.GetQuoteBlob())

	pckCertBytes, _ := utils.GetCertPemData(quoteObj.GetQuotePckCertObj())

	scsClient := mocks.NewClientMock(http.StatusOK)
	testConfig := config.Load(testConfigFilePath)

	got = NewPCKCertObj(pckCertBytes, scsClient, nil)
	assert.Nil(t, got)

	got = NewPCKCertObj(pckCertBytes, nil, testConfig)
	assert.Nil(t, got)

	got = NewPCKCertObj(pckCertBytes, scsClient, testConfig)
	assert.NotNil(t, got)

	// test with negative clients

	scsClient = mocks.NewClientMock(400)
	got = NewPCKCertObj(pckCertBytes, scsClient, testConfig)
	assert.Nil(t, got)

	scsClient = mocks.NewClientMock(401)
	got = NewPCKCertObj(pckCertBytes, scsClient, testConfig)
	assert.Nil(t, got)

	scsClient = mocks.NewClientMock(204)
	got = NewPCKCertObj(pckCertBytes, scsClient, testConfig)
	assert.Nil(t, got)

	scsClient = mocks.NewClientMock(202)
	got = NewPCKCertObj(pckCertBytes, scsClient, testConfig)
	assert.Nil(t, got)

	// Remove test files and the end.
	os.Remove(testCertPemFile)
	os.Remove(privatekeyLocation)
}
