func TestTcbInfoStruct_GetTcbInfoInterCaList(t *testing.T) {

	var tcsInfodata TcbInfoJSON

	json.Unmarshal(tcbInfoJson, &tcsInfodata)

	tcbInfoStruct := &TcbInfoStruct{
		TcbInfoData: tcsInfodata,
	}

	tcbInfoInterCaList := tcbInfoStruct.GetTcbInfoInterCaList()
	assert.NotNil(t, tcbInfoInterCaList)

	tcbInfoRootCaList := tcbInfoStruct.GetTcbInfoRootCaList()
	assert.NotNil(t, tcbInfoRootCaList)

	tcbInfoIssueDate := tcbInfoStruct.GetTcbInfoIssueDate()
	assert.NotNil(t, tcbInfoIssueDate)

	tcbInfoNextUpdate := tcbInfoStruct.GetTcbInfoNextUpdate()
	assert.NotNil(t, tcbInfoNextUpdate)

	tcbInfoFmspc := tcbInfoStruct.GetTcbInfoFmspc()
	assert.NotNil(t, tcbInfoFmspc)

	status := tcbInfoStruct.GetTcbUptoDateStatus([]byte("testdetails1234567890"))
	assert.NotNil(t, status)
	// TCBLevels need to updated
	tcbInfoStruct.DumpTcbInfo()
}

func TestNewTcbInfo(t *testing.T) {
	scsClient := mocks.NewClientMock(http.StatusOK)
	testConfig := config.Load(testConfigFilePath)

	_, err := NewTcbInfo("testfmspctestValue", testConfig, scsClient)
	assert.Nil(t, err)

	_, err = NewTcbInfo("testfmspc", testConfig, scsClient)
	assert.NotNil(t, err)
}

func TestCompareTcbComponents(t *testing.T) {

	pckComponents := []byte("testvalue")
	pckpcesvn := uint16(12)
	tcbComponents := []byte("testvalue")
	tcbpcesvn := uint16(2)
	got := compareTcbComponents(pckComponents, pckpcesvn, tcbComponents, tcbpcesvn)
	assert.NotNil(t, got)

	pckComponents = []byte("testvalue1234567")
	tcbComponents = []byte("testvalue0123456")
	got = compareTcbComponents(pckComponents, pckpcesvn, tcbComponents, tcbpcesvn)
	assert.NotNil(t, got)

	pckpcesvn = uint16(1)
	tcbpcesvn = uint16(2)
	got = compareTcbComponents(pckComponents, pckpcesvn, tcbComponents, tcbpcesvn)
	assert.NotNil(t, got)
}

